// ContentModel
async deleteOlderThan(days: number): Promise<number> {
  const result = await this.db.run(
    'DELETE FROM content WHERE generated_at < DATE_SUB(NOW(), INTERVAL ? DAY)',
    [days]
  );
  return result.changes;
}
```

